  const handleChangeLanguage = () => {
    // ваша логика для изменения языка
  };

  return (
    <div>
      <h2>Settings</h2>
      <button onClick={toggleTheme}>Toggle Theme</button>
      <button onClick={handleChangeLanguage}>Change Language</button>
    </div>
  );
};

export default SettingsForm;
