import React from 'react';
import ReactDOM from 'react-dom';
import { useTheme } from 'next-themes';
import App from './App';
import image from "next/image";


// Определение переменной systemTheme
const systemTheme = 'light'; // или что-то другое

export default function Home() {
  const { theme, setTheme } = useTheme();
  const systemTheme = "light"; // определение systemTheme

  const currentTheme = theme === 'system' ? systemTheme : theme;

  return (
    <div className="grid place-items-center h-screen">
      <div>
        <div className="flex justify-center">
          {currentTheme === 'dark' ? (
            <>
              You, yesterday • Add toggle and dark mode functionality
              <button
                className="bg-black-700 hover:bg-black w-28 rounded-md border-purple-400 border-2 p-4"
                onClick={() => setTheme("light")}
              >
                <image src="../public/sun.svg" alt="logo" height="50px" width="50px" />
              </button>
              <button
                className="bg-gray-100 w-28 rounded-md border-purple-400 border-2 p-4 hover:bg-gray-300"
                onClick={() => setTheme('dark')}
              >
                <image src="../public/moon.svg" alt="logo" height="50px" width="50px" />
              </button>
            </>
          ) : null}
          {/* <App /> */}
        </div>
      </div>
    </div>
  );
}


ReactDOM.render(<Home />, document.getElementById('root'));
