import React, { useState } from 'react';
import { SettingsProvider } from './context/settingsContext';
import SettingsForm from './components/SettingsForm'; 
import ComponentA from './components/ComponentA'; 
import ComponentB from './components/ComponentB'; 
import Tooltip from './components/Tooltip'; 
import ThemeToggle from './components/ThemeToggle'; // Путь к вашему компоненту ThemeToggle
import { ThemeProvider } from 'next-themes';
import './index.css';

const App = ({ Component, pageProps }) => {
  const [language, setLanguage] = useState('ru');

  const toggleLanguage = () => {
    setLanguage(prevLanguage => prevLanguage === 'ru' ? 'en' : 'ru');
  };

  return (
    <ThemeProvider enableSystem={true} attribute="class">
      <SettingsProvider>
        <div>
          <SettingsForm />
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
          <div>
            <ComponentA isTrackingTime={false} />
          </div>
          <div>
            <ComponentB isTrackingTime={true} />
          </div>
        </div>
        <div className="container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
          <Tooltip position="top" content="Tooltip top">
            <button>Top</button>
          </Tooltip>
          <Tooltip position="right" content="Tooltip right">
            <button>Right</button>
          </Tooltip>
          <Tooltip position="bottom" content="Tooltip bottom">
            <button>Bottom</button>
          </Tooltip>
          <Tooltip position="left" content="Tooltip left">
            <button>Left</button>
          </Tooltip>
        </div>
        <div className="toggle-container">
          <ThemeToggle />
        </div>
        <div className="text-container">
          {language === 'ru' ? (
            <p>Языки программирования — это всегда некоторые модели (виртуальные вычислительные машины), позволяющие наиболее эффективно использовать возможности вычислительных средств, существенные для конкретных областей применения.</p>
          ) : (
            <p>Programming languages are always some models (virtual computing machines) that allow the most efficient use of computing capabilities that are essential for specific applications.</p>
          )}
        </div>
        <button onClick={toggleLanguage}>Change Language</button>
      </SettingsProvider>
    </ThemeProvider>
  );
};

export default App;
