// Tooltip.js
import React, { useState } from 'react';
import PropTypes from 'prop-types';
// import './Tooltip.css';

const Tooltip = ({ position, content, children }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [tooltipPosition, setTooltipPosition] = useState({});

  const showTooltip = (e) => {
    setIsVisible(true);
    setTooltipPosition({
      top: position === 'top' ? e.clientY - 10 : null,
      right: position === 'right' ? window.innerWidth - e.clientX + 10 : null,
      bottom: position === 'bottom' ? window.innerHeight - e.clientY + 10 : null,
      left: position === 'left' ? e.clientX - 10 : null,
    });
  };

  const hideTooltip = () => {
    setIsVisible(false);
  };

  return (
    <div className="tooltip-container" onMouseEnter={showTooltip} onMouseLeave={hideTooltip}>
      {isVisible && (
        <div
          className={`tooltip tooltip-${position}`}
          style={{ ...tooltipPosition }}
        >
          {content}
        </div>
      )}
      {children}
    </div>
  );
};

Tooltip.propTypes = {
  position: PropTypes.oneOf(['top', 'right', 'bottom', 'left']),
  content: PropTypes.string,
  children: PropTypes.node.isRequired,
};

Tooltip.defaultProps = {
  position: 'top',
  content: '',
};

export default Tooltip;
