import { useContext } from 'react';
import { ThemeContext } from '../context/settingsContext';

const SettingsForm = () => {
  const { theme, setTheme } = useContext(ThemeContext);

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <button onClick={toggleTheme}>Toggle Theme</button>
  );
};
