// Tooltip.js
import React, { useState } from 'react';

const Tooltip = ({ children }) => {
  const [isVisible, setIsVisible] = useState(false);

  const showTooltip = () => {
    setIsVisible(true);
  };

  const hideTooltip = () => {
    setIsVisible(false);
  };

  return children({ showTooltip, hideTooltip, isVisible });
};

export default Tooltip;
