// App.js
import React from 'react';
import { SettingsProvider } from './context/settingsContext';
import SettingsForm from './components/SettingsForm';
import ComponentA from './components/ComponentA';
import ComponentB from './components/ComponentB';
import Tooltip from './components/Tooltip';
import './index.css';

const App = () => {
  return (
    <SettingsProvider>
      <div>
        <h1>Time Tracking</h1>
        {/* Добавляем пропс isTrackingTime={false} для ComponentA */}
        <ComponentA isTrackingTime={false} />
        {/* Добавляем пропс isTrackingTime={true} для ComponentB */}
        <ComponentB isTrackingTime={true} />
      </div>
      <div>
        <h1>Settings</h1>
        <SettingsForm />
      </div>
      <div>
        <h1>Tooltip Example</h1>
        <Tooltip position="top" content="Tooltip top">
          <button>Top</button>
        </Tooltip>
        <Tooltip position="right" content="Tooltip right">
          <button>Right</button>
        </Tooltip>
        <Tooltip position="bottom" content="Tooltip bottom">
          <button>Bottom</button>
        </Tooltip>
        <Tooltip position="left" content="Tooltip left">
          <button>Left</button>
        </Tooltip>
      </div>
    </SettingsProvider>
  );
};

export default App;
