import React, { useState, createContext } from 'react';
import { SettingsProvider } from './context/settingsContext';
import SettingsForm from './components/SettingsForm'; 
import ComponentA from './components/ComponentA'; 
import ComponentB from './components/ComponentB'; 
import Tooltip from './components/Tooltip'; 
import ThemeToggle from './components/ThemeToggle'; 
import { ThemeProvider } from 'next-themes';
import './index.css';

export const LanguageContext = createContext();

const App = ({ Component, pageProps }) => {
  const [language, setLanguage] = useState('en'); // Default language is English

  return (
    <ThemeProvider enableSystem={true} attribute="class">
      <LanguageContext.Provider value={{ language, setLanguage }}>
        <SettingsProvider>
          <div>
            <SettingsForm />
          </div>
          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
            <div>
              <ComponentA isTrackingTime={false} />
            </div>
            <div>
              <ComponentB isTrackingTime={true} />
            </div>
          </div>
          <div className="container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
            <Tooltip position="top" content={language === 'en' ? 'Tooltip top' : 'Подсказка сверху'}>
              <button>{language === 'en' ? 'Top' : 'Сверху'}</button>
            </Tooltip>
            <Tooltip position="right" content={language === 'en' ? 'Tooltip right' : 'Подсказка справа'}>
              <button>{language === 'en' ? 'Right' : 'Справа'}</button>
            </Tooltip>
            <Tooltip position="bottom" content={language === 'en' ? 'Tooltip bottom' : 'Подсказка снизу'}>
              <button>{language === 'en' ? 'Bottom' : 'Снизу'}</button>
            </Tooltip>
            <Tooltip position="left" content={language === 'en' ? 'Tooltip left' : 'Подсказка слева'}>
              <button>{language === 'en' ? 'Left' : 'Слева'}</button>
            </Tooltip>
          </div>
          <div className="toggle-container">
            <ThemeToggle />
          </div>
        </SettingsProvider>
      </LanguageContext.Provider>
    </ThemeProvider>
  );
};

export default App;
