// App.js
import React, { createContext, useState, useContext } from 'react';
import { ThemeProvider } from 'next-themes';
import './index.css';

const SettingsContext = createContext();

const SettingsProvider = ({ children }) => {
  const [theme, setTheme] = useState('light');

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  return (
    <SettingsContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </SettingsContext.Provider>
  );
};

const useSettings = () => useContext(SettingsContext);

const App = ({ Component, pageProps }) => {
  return (
    <ThemeProvider enableSystem={true} attribute="class">
      <SettingsProvider>
        <div>
          <SettingsForm />
        </div>
        <div
          className={`container ${
            useSettings().theme === 'dark' ? 'dark' : ''
          }`}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            gap: '20px',
            marginTop: '20px',
          }}
        >
          <div>
            <ComponentA isTrackingTime={false} />
          </div>
          <div>
            <ComponentB isTrackingTime={true} />
          </div>
        </div>
        <div
          className={`container ${
            useSettings().theme === 'dark' ? 'dark' : ''
          }`}
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            gap: '20px',
            marginTop: '20px',
          }}
        >
          <Tooltip position="top" content="Tooltip top">
            <button>Top</button>
          </Tooltip>
          <Tooltip position="right" content="Tooltip right">
            <button>Right</button>
          </Tooltip>
          <Tooltip position="bottom" content="Tooltip bottom">
            <button>Bottom</button>
          </Tooltip>
          <Tooltip position="left" content="Tooltip left">
            <button>Left</button>
          </Tooltip>
        </div>
      </SettingsProvider>
    </ThemeProvider>
  );
};

export default App;
