import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

// Example translations for English and Russian
const resources = {
  en: {
    translation: {
      'Time spent on Page: {time Spent} seconds': 'Time spent on Page: {time Spent} seconds',
      'Current Time: {current Time}': 'Current Time: {current Time}',
    },
  },
  ru: {
    translation: {
      'Time spent on Page: {time Spent} seconds': 'Время, проведенное на странице: {time Spent} секунды',
      'Current Time: {current Time}': 'Текущее время: {current Time}',
    },
  },
};

i18n
  .use(initReactI18next)
  .init({
    resources,
    lng: 'ru', // Default language
    interpolation: {
      escapeValue: false,
    },
  });

export default i18n;
