import React from 'react';
import { SettingsProvider } from './context/settingsContext';
import SettingsForm from './components/SettingsForm'; 
import ComponentA from './components/ComponentA'; 
import ComponentB from './components/ComponentB'; 
import Tooltip from './components/Tooltip'; 
import ThemeToggle from './components/ThemeToggle'; // Путь к вашему компоненту ThemeToggle
import { ThemeProvider } from 'next-themes';
import './index.css';

const translations = {
  en: {
    top: 'Top',
    right: 'Right',
    bottom: 'Bottom',
    left: 'Left',
    message: 'Programming languages are always some models (virtual computing machines) that allow the most efficient use of computing capabilities that are essential for specific applications.',
    changeLanguage: 'Change Language',
  },
  ru: {
    top: 'Сверху',
    right: 'Справа',
    bottom: 'Снизу',
    left: 'Слева',
    message: 'Языки программирования — это всегда некоторые модели (виртуальные вычислительные машины), позволяющие наиболее эффективно использовать возможности вычислительных средств, существенные для конкретных областей применения.',
    changeLanguage: 'Изменить язык',
  },
};

const App = ({ Component, pageProps }) => {
  return (
    <ThemeProvider enableSystem={true} attribute="class">
      <SettingsProvider>
        <div>
          <SettingsForm />
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
          <div>
            <ComponentA isTrackingTime={false} />
          </div>
          <div>
            <ComponentB isTrackingTime={true} />
          </div>
        </div>
        <div className="container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
          <Tooltip position="top" content={translations[language].top}>
            <button>{translations[language].top}</button>
          </Tooltip>
          <Tooltip position="right" content={translations[language].right}>
            <button>{translations[language].right}</button>
          </Tooltip>
          <Tooltip position="bottom" content={translations[language].bottom}>
            <button>{translations[language].bottom}</button>
          </Tooltip>
          <Tooltip position="left" content={translations[language].left}>
            <button>{translations[language].left}</button>
          </Tooltip>
        </div>
        <div className="toggle-container">
          <ThemeToggle />
        </div>
        <div>{translations[language].message}</div>
      </SettingsProvider>
    </ThemeProvider>
  );
};

export default App;
