import React, { useState } from 'react';
import { SettingsProvider } from './context/settingsContext';
import SettingsForm from './components/SettingsForm'; 
import ComponentA from './components/ComponentA'; 
import ComponentB from './components/ComponentB'; 
import Tooltip from './components/Tooltip'; 
import ThemeToggle from './components/ThemeToggle'; // Путь к вашему компоненту ThemeToggle
import { ThemeProvider } from 'next-themes';
import './index.css';

const App = ({ Component, pageProps }) => {
  const [language, setLanguage] = useState('ru');

  const toggleLanguage = () => {
    setLanguage(language === 'ru' ? 'en' : 'ru');
  };

  const getText = (text) => {
    if (language === 'en') {
      switch (text) {
        case 'Языки программирования — это всегда некоторые модели (виртуальные вычислительные машины), позволяющие наиболее эффективно использовать возможности вычислительных средств, существенные для конкретных областей применения.':
          return 'Programming languages are always some models (virtual computing machines) that allow the most efficient use of computing capabilities that are essential for specific applications.';
        case 'Сверху':
          return 'Top';
        case 'Справа':
          return 'Right';
        case 'Снизу':
          return 'Bottom';
        case 'Слева':
          return 'Left';
        default:
          return text;
      }
    } else {
      return text;
    }
  };

  return (
    <ThemeProvider enableSystem={true} attribute="class">
      <SettingsProvider>
        <div>
          <SettingsForm />
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
          <div>
            <ComponentA isTrackingTime={false} />
          </div>
          <div>
            <ComponentB isTrackingTime={true} />
          </div>
        </div>
        <div className="container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '20px', marginTop: '20px' }}>
          <Tooltip position="top" content={getText("Tooltip top")}>
            <button>{getText("Top")}</button>
          </Tooltip>
          <Tooltip position="right" content={getText("Tooltip right")}>
            <button>{getText("Right")}</button>
          </Tooltip>
          <Tooltip position="bottom" content={getText("Tooltip bottom")}>
            <button>{getText("Bottom")}</button>
          </Tooltip>
          <Tooltip position="left" content={getText("Tooltip left")}>
            <button>{getText("Left")}</button>
          </Tooltip>
        </div>
        <div className="toggle-container">
          <ThemeToggle />
        </div>
        <button onClick={toggleLanguage}>Switch Language</button>
      </SettingsProvider>
    </ThemeProvider>
  );
};

export default App;
